libraryDependencies ++= Seq(
  // distage core library
  "io.7mind.izumi" %% "distage-core" % "0.10.6-SNAPSHOT",
  // distage-testkit for ScalaTest
  "io.7mind.izumi" %% "distage-testkit-scalatest" % "0.10.6-SNAPSHOT" % Test,
  // distage-framework: Roles, Entrypoints, Effect modules
  "io.7mind.izumi" %% "distage-framework" % "0.10.6-SNAPSHOT",
  // Typesafe Config support
  "io.7mind.izumi" %% "distage-extension-config" % "0.10.6-SNAPSHOT",
  // Classpath discovery support
  "io.7mind.izumi" %% "distage-extension-plugins" % "0.10.6-SNAPSHOT",
